    public static boolean isValidEmail(String email){
        Pattern ptr = Pattern.compile(VALID_EMAIL_REGEX);
        if(ptr.matcher(email).matches()){
            return true;
        }else{
            return false;
        }
    }


    public static Boolean isBlankString(String str){
        Boolean isEmpty = false;
        if(str == null || str.isEmpty()){
            isEmpty = true;
        }
        return isEmpty;
    }

    public static Boolean isValidNumber(String str){
        Boolean isValidNumber = true;
        try{
            Integer integer = Integer.parseInt(str);
        }catch(NumberFormatException np){
            isValidNumber = false;
        }finally{
            return isValidNumber;
        }
    }

    private static boolean isValidPhoneNumber(String phoneNo) {
        //validate phone numbers of format "1234567890"
        if (phoneNo.matches("\\d{10}")) return true;
            //validating phone number with -, . or spaces
        else if(phoneNo.matches("\\d{3}[-\\.\\s]\\d{3}[-\\.\\s]\\d{4}")) return true;
            //validating phone number with extension length from 3 to 5
        else if(phoneNo.matches("\\d{3}-\\d{3}-\\d{4}\\s(x|(ext))\\d{3,5}")) return true;
            //validating phone number where area code is in braces ()
        else if(phoneNo.matches("\\(\\d{3}\\)-\\d{3}-\\d{4}")) return true;
            //return false if nothing matches the input
        else return false;

    }

    public void validateContactDetails(ContactDetails contactDetails) throws HMSRuntimeException{

        if(null == contactDetails){
            throw new HMSRuntimeException(HMSErrorInfo.getNewErrorInfo(HMSErrorCodes.INVALID_ADDRESS_DETAILS,"Contact Details are missing or null"));
        }

        if(!isValidEmail(contactDetails.getPrimaryEmail())){
            throw new HMSRuntimeException(HMSErrorInfo.getNewErrorInfo(HMSErrorCodes.INVALID_EMAIL,"Invalid Email Address"));
        }


        if(!isValidPhoneNumber(contactDetails.getPrimaryPhone())){
            throw new HMSRuntimeException(HMSErrorInfo.getNewErrorInfo(HMSErrorCodes.INVALID_PHONE,"Invalid Phone Number"));
        }


        if(!isValidPhoneNumber(contactDetails.getPrimaryMobileNumber())){
            throw new HMSRuntimeException(HMSErrorInfo.getNewErrorInfo(HMSErrorCodes.INVALID_MOBILE_PHONE,"Invalid Mobile Phone."));
        }

    }

    public void validateAddress(Address address) throws HMSRuntimeException{

        if(null == address){
            throw new HMSRuntimeException(HMSErrorInfo.getNewErrorInfo(HMSErrorCodes.INVALID_ADDRESS_DETAILS,"Address Details are missing or null"));
        }

        if(isBlankString(address.getAddressLine1())){
            throw new HMSRuntimeException(HMSErrorInfo.getNewErrorInfo(HMSErrorCodes.INVALID_ADDRESS_DETAILS,"Address Details are missing or null"));
        }

        if(isBlankString(address.getCity()) ||
                isBlankString(address.getState()) ||
                isBlankString(address.getCountry()) ||
                isBlankString(address.getPinCode())){
            throw new HMSRuntimeException(HMSErrorInfo.getNewErrorInfo(HMSErrorCodes.INVALID_ADDRESS_DETAILS,"Address Details - City ,State, Country or Pin-Code are missing"));
        }

    }
